package br.com.joaodartora.certificatevalidator.stub;

import org.jose4j.keys.X509Util;
import org.jose4j.lang.JoseException;

import java.security.cert.X509Certificate;

public class X509CertificateStub {

    private static X509Util x509Util = new X509Util();
    public static String BASE_64_ROOT_CERTIFICATE = "";
    public static String BASE_64_SUBCA_CERTIFICATE = "";
    public static String BASE_64_LEAF_CERTIFICATE = "";

    public static X509Certificate buildRootCertificate() throws JoseException {
        return x509Util.fromBase64Der(BASE_64_ROOT_CERTIFICATE);
    }

    public static X509Certificate buildSubCACertificate() throws JoseException {
        return x509Util.fromBase64Der(BASE_64_SUBCA_CERTIFICATE);
    }

    public static X509Certificate buildLeafCertificate() throws JoseException {
        return x509Util.fromBase64Der(BASE_64_LEAF_CERTIFICATE);
    }

    public static X509Certificate buildExpiredCertificate() throws JoseException {
        var base64ExpiredCertificate = "";
        return x509Util.fromBase64Der(base64ExpiredCertificate);
    }

    public static X509Certificate buildCertificateWithoutNewtonCustomOID() throws JoseException {
        var base64NotNewtonCertificate = "";
        return x509Util.fromBase64Der(base64NotNewtonCertificate);
    }

    public static X509Certificate buildTestLeafCertificate() throws JoseException {
        var base64ExpiredCertificate = " ";
        return x509Util.fromBase64Der(base64ExpiredCertificate);
    }
}
